//     return (
//     <SearchInfo>
//       <SearchInfoTitle>
//         热门搜索
//         <SearchInfoSwitch>
//           换一批
//         </SearchInfoSwitch>
//       </SearchInfoTitle>
//       <SearchInfoList>
//         <SearchInfoItem>
//           教育
//         </SearchInfoItem>
//       </SearchInfoList>
//     </SearchInfo>
//     )
//   } else {
//     return null;
//   }
// }

class Header extends Component {
  constructor(props){
    super(props);
  }

  getListArea(show){
  if (show) {
    return (
    <SearchInfo>
      <SearchInfoTitle>
        热门搜索
        <SearchInfoSwitch>
          换一批
        </SearchInfoSwitch>
      </SearchInfoTitle>
      <SearchInfoList>
        <SearchInfoItem>
          教育
        </SearchInfoItem>
      </SearchInfoList>
    </SearchInfo>
    )
  } else {
    return null;
  }
  }

  render() {
    return (
      <HeaderWrapper>
        <Logo></Logo>
        <Nav>
          <NavItem className='left active'>
            首页
          </NavItem>
          <NavItem className='left'>
            下载App
          </NavItem>
          <NavItem className='right'>
            登录
          </NavItem>
          <NavItem className='right'>
            <i className="iconfont">&#xe636;</i>
          </NavItem>
          <SearchWrapper>
            <CSSTransition
              in={this.props.focused}
              timeout={200}
              classNames="slide"
            >
                <NavSearch
                className={this.props.focused ? 'focused' : ''}
                onFocus={this.props.handleInputFocus}
                onBlur={this.props.handleInputBlur}
                >

                </NavSearch>
            </CSSTransition>
            <i className={this.props.focused ? 'focused iconfont' : 'iconfont'}>&#xe60b;</i>
            {this.getListArea(this.props.focused )}
          </SearchWrapper>
        </Nav>
        <Addition>
          <Button className="writting">
            <i className="iconfont">&#xe708;</i>
            写文章
          </Button>
          <Button className="reg">注册</Button>
        </Addition>
      </HeaderWrapper>
    )
  }

}

const mapStateToProps = (state) => {
    return {
      // immutable对象, state也变成了一个immutable对象
      focused: state.getIn(['header', 'focused'])
    }
    // focused: state.get('header').get('focused') 这种方式等价于以上方法
    // focused: state.header.get('focused')
    // focused: state.header.focused;
}

const mapDispathToProps = (dispatch) => {
    return {
        handleInputFocus(){
            // const action = {
            //     type: 'search_focus'
            // }
            dispatch(actionCreators.searchFocus());
        },
        handleInputBlur(){
            // const action = {
            //     type: 'search_blur'
            // }
            dispatch(actionCreators.searchBlur());
        }
    }
}

export default connect(mapStateToProps, mapDispathToProps) (Header);
